            $('#max-size').html((maxsize / 1024).toFixed(2));

            $('#pic').change(function () {

                $('#message').empty();

                var file = this.files[0];
                var match = [""image/jpeg"", ""image/png"", ""image/jpg""];

                if (!((file.type == match[0]) || (file.type == match[1]) || (file.type == match[2]))) {
